import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import Badge from 'react-bootstrap/Badge'
import './moviecard.css' // Create this for custom styles/animations

const MovieCard = ({
  poster = 'data:image/jpeg;base64,',
  title = 'Back to the Future',
  genre = 'Time Travel',
  duration = '1h 56m',
  onWatch = () => alert('Watch clicked!')
}) => (
  <Card className="movie-card shadow-sm border-0">
    <div className="movie-card-img-wrapper">
      <Card.Img variant="top" src={poster} alt={title} className="movie-card-img" />
    </div>
    <Card.Body>
      <Card.Title className="mb-2">{title}</Card.Title>
      <div className="mb-2">
        <Badge bg="primary" className="me-2">{genre}</Badge>
        <Badge bg="secondary">{duration}</Badge>
      </div>
      <Button
        variant="success"
        className="w-100 movie-card-btn"
        onClick={onWatch}
      >
        Watch
      </Button>
    </Card.Body>
  </Card>
)

export default MovieCard